import React, {useState} from "react";

import "./ExpenseForm.css";

//componente usado para formatar e estilizar os inputs e botões que estarão presentes no form para add uma nova despesa

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    {/*não importa quantas vezes esse componente seja atualizado, esse estado será armazenado e sobreviverá*/}
    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    };

    return (
    <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangeHandler} />
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" />
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2022-12-31" />
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </div>
    </form>
    );
}

export default ExpenseForm;
