//importando o arquivo CSS para estilizar esse componente
import './ExpenseItem.css'

//como "class" é uma palavra reservada em JS, usamos "className" para nomear as classes 
function ExpenseItem(props) {
    const dia = props.date.toLocaleString('pt-BR',{day: 'nume2-digit'});
    const mes = props.date.toLocaleString('pt-BR',{month: 'long'});
    const ano = props.date.getFullYear();

    return (
        <div className='expense-item'>
            <div>
            <div>{dia}</div>
            <div>{mes}</div>
            <div>{ano}</div>
            </div>
            <div className='expense-item__description'>
                <h2>{props.title}</h2> 
                <div className='expense-item__price'>R${props.amount}</div>
            </div>
        </div>
  );
}

export default ExpenseItem;
