import React, {useState} from "react";

import "./ExpenseForm.css";

//componente usado para formatar e estilizar os inputs e botões que estarão presentes no form para add uma nova despesa

const ExpenseForm = () => {

    {/*o tipo padrão do valor de um input é string*/}

    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    //colancando os 3 estados acima em um só dentro de um objeto

    //const [userInput, setUserInput] = useState({
    //    enteredTitle: '',
    //    enteredAmount: '',
    //    enteredDate: '',
    //})

    {/*não importa quantas vezes esse componente seja atualizado, esse estado será armazenado e sobreviverá*/}
    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
        //é necessário usar o operador destructuring para que os outros dados não se percam ao colocar multiplos estados em um só
       // setUserInput({
         //   ...userInput,
        //    enteredTitle: event.target.value,        
        //}),
        //sempre usar ESSA abordagem quando o novo estado depende de um estado anterior
       // setUserInput((prevState) => {
      //      return {...prevState, enteredTitle: event.target.value};
     //   })
    };
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    };
    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    };

    return (
    <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangeHandler} />
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler} />
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler} />
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </div>
    </form>
    );
}

export default ExpenseForm;
